<script>
	function initialize() {
		var myLatlng1 = new google.maps.LatLng(48.575620,13.458776);
		var myLatlng2 = new google.maps.LatLng(48.859682,13.395594);
		var mapOptions1 = {
			zoom: 15,
			center: myLatlng1,
			mapTypeId: google.maps.MapTypeId.ROADMAP,
			scrollwheel: false
		}
		var mapOptions2 = {
			zoom: 15,
			center: myLatlng2,
			mapTypeId: google.maps.MapTypeId.ROADMAP,
			scrollwheel: false
		}

		var map1 = new google.maps.Map(document.getElementById('google-map1'), mapOptions1);
		var map2 = new google.maps.Map(document.getElementById('google-map2'), mapOptions2);

		var contentString1 = '<div class="marker-div">'+
				'<strong>Sanitätshaus Fürst Passau</strong><br>' +
				'Kleine Klingergasse 10<br>' +
				'94032 Passau<br>' +
				'</div>';
		var contentString2 = '<div class="marker-div">'+
				'<strong>Sanitätshaus Fürst Grafenau</strong><br>' +
				'Schwarzmaierstraße 10<br>' +
				'94481 Grafenau<br>' +
				'</div>';

		var infowindow1 = new google.maps.InfoWindow({
			content: contentString1
		});
		var infowindow2 = new google.maps.InfoWindow({
			content: contentString2
		});

		var marker1 = new google.maps.Marker({
			position: myLatlng1,
			map: map1,
		});
		var marker2 = new google.maps.Marker({
			position: myLatlng2,
			map: map2,
		});
		
		infowindow1.open(map1, marker1);
		infowindow2.open(map2, marker2);
		
		google.maps.event.addListener(marker1, 'click', function() {
			infowindow.open(map1, marker1);
		});
		google.maps.event.addListener(marker2, 'click', function() {
			infowindow.open(map2, marker2);
		});
	}
	google.maps.event.addDomListener(window, 'load', initialize);
</script>